export type ROMALevel = "L0" | "L1" | "L2" | "L3" | "L4";
export type Jurisdiction = "india" | "uae" | "saudi_arabia" | "singapore" | "global";

export interface Market360Agent {
  id: string;
  name: string;
  vertical: Vertical;
  romaLevel: ROMALevel;
  description: string;
  capabilities: string[];
  tools: string[];
  languages: SupportedLanguage[];
  jurisdictions: Jurisdiction[];
  systemPrompt: string;
}

