export function generateAllAgents(): Market360Agent[] {
  const allAgentConfigs = [
    ...SOCIAL_AGENTS,
    ...SEO_AGENTS,
    ...WEB_AGENTS,
    ...SALES_AGENTS,
    ...WHATSAPP_AGENTS,
    ...LINKEDIN_AGENTS,
    ...PERFORMANCE_AGENTS
  ];
  
  return allAgentConfigs.map(config => ({
    ...config,
    systemPrompt: generateAgentSystemPrompt(config)
  }));
}

export const ALL_MARKET360_AGENTS = generateAllAgents();

export function getAgentsByVertical(vertical: Vertical): Market360Agent[] {
  return ALL_MARKET360_AGENTS.filter(a => a.vertical === vertical);
}

export function getAgentsByROMALevel(level: ROMALevel): Market360Agent[] {
  return ALL_MARKET360_AGENTS.filter(a => a.romaLevel === level);
}

export function getAgentById(id: string): Market360Agent | undefined {
  return ALL_MARKET360_AGENTS.find(a => a.id === id);
}

export function getAgentStats() {
  const byVertical: Record<Vertical, number> = { social: 0, seo: 0, web: 0, sales: 0, whatsapp: 0, linkedin: 0, performance: 0 };
  const byROMA: Record<ROMALevel, number> = { L0: 0, L1: 0, L2: 0, L3: 0, L4: 0 };
  
  ALL_MARKET360_AGENTS.forEach(agent => {
    byVertical[agent.vertical]++;
    byROMA[agent.romaLevel]++;
  });
  
  return {
    total: ALL_MARKET360_AGENTS.length,
    byVertical,
    byROMA,
    targetTotal: 267
  };
}
